// Extract notifications array from response
const response = $input.first().json;
const notifications = response.value || [];

console.log(`Found ${notifications.length} pending notifications`);

// Return array of notifications (each will be processed separately)
if (notifications.length === 0) {
  return [];
}

return notifications.map(notification => ({ json: notification }));

// Transform data for status change email
// Input: Notification data + Candidate details
// Output: Email data object

const notification = $input.first().json;
const candidateData = $input.all()[1].json;

// Extract candidate information
const candidate = {
  firstName: candidateData.firstName || '',
  lastName: candidateData.lastName || '',
  email: candidateData.email || '',
  fullName: `${candidateData.firstName} ${candidateData.lastName}`.trim()
};

// Extract status change information from notification
const oldStatus = notification.oldStatus || 'Unknown';
const newStatus = notification.newStatus || 'Unknown';
const changedAt = notification.changedAt ? new Date(notification.changedAt).toLocaleString() : new Date().toLocaleString();
const changedBy = notification.changedBy || 'System';

// Determine status color for email styling
const statusColors = {
  'Applied': '#3498db',
  'Under Review': '#f39c12',
  'Shortlisted': '#9b59b6',
  'Interview Scheduled': '#e67e22',
  'Interviewed': '#16a085',
  'Offer Extended': '#27ae60',
  'Hired': '#2ecc71',
  'Rejected': '#e74c3c',
  'Withdrawn': '#95a5a6'
};

const statusColor = statusColors[newStatus] || '#34495e';

// Read email template
const fs = require('fs');
const path = require('path');
const templatePath = path.join($env.TEMPLATE_PATH, 'status-changed.html');
let emailHtml = '';

try {
  emailHtml = fs.readFileSync(templatePath, 'utf8');

  // Replace template variables
  emailHtml = emailHtml
    .replace(/{{candidateName}}/g, candidate.fullName)
    .replace(/{{firstName}}/g, candidate.firstName)
    .replace(/{{oldStatus}}/g, oldStatus)
    .replace(/{{newStatus}}/g, newStatus)
    .replace(/{{statusColor}}/g, statusColor)
    .replace(/{{changedAt}}/g, changedAt)
    .replace(/{{changedBy}}/g, changedBy)
    .replace(/{{year}}/g, new Date().getFullYear());
} catch (error) {
  // Fallback to simple HTML if template not found
  emailHtml = `
    <html>
      <body style="font-family: Arial, sans-serif;">
        <h2>Application Status Update</h2>
        <p>Dear ${candidate.fullName},</p>
        <p>Your application status has been updated:</p>
        <p>
          <strong>Previous Status:</strong> ${oldStatus}<br>
          <strong>New Status:</strong> <span style="color: ${statusColor};">${newStatus}</span>
        </p>
        <p>Changed by ${changedBy} on ${changedAt}</p>
        <p>Best regards,<br>CV Sorting System</p>
      </body>
    </html>
  `;
}

// Return email data
return {
  json: {
    to: candidate.email,
    subject: `Application Status Update: ${newStatus}`,
    html: emailHtml,
    candidateId: notification.candidateId,
    notificationId: notification.ID,
    candidateName: candidate.fullName,
    oldStatus: oldStatus,
    newStatus: newStatus
  }
};

// Collect and summarize results from all processed notifications
const items = $input.all();

const summary = {
  totalProcessed: items.length,
  successful: items.filter(item => item.json.success !== false).length,
  failed: items.filter(item => item.json.success === false).length,
  timestamp: new Date().toISOString(),
  notifications: items.map(item => ({
    candidateId: item.json.candidateId,
    email: item.json.to,
    status: item.json.success !== false ? 'sent' : 'failed'
  }))
};

console.log('Pending notifications workflow completed:', summary);

return { json: summary };

// Handle case when no pending notifications
console.log('No pending notifications found');

return {
  json: {
    message: 'No pending notifications found',
    timestamp: new Date().toISOString()
  }
};

// Log error for debugging
const error = $input.first().json;
console.error('Workflow error:', error);

// Mark notification as failed and continue
return {
  json: {
    success: false,
    message: error.message || 'Error processing notification',
    error: error,
    timestamp: new Date().toISOString(),
    candidateId: error.candidateId || 'unknown'
  }
};
